package Method;

public class K {

	public static int add(int a,int b,int h) {
		// TODO Auto-generated method stub
		int T=(a+b/2)*h;
		return T;
	}
	public static void main(String[] args) {
		System.out.println("Start");
		int Z = K.add(5,5,10);
		System.out.println(Z);
		System.out.println("End");
	}

}
